import React,{useEffect, useState} from "react";

const Lifecycle = () => {

    const [count, setCount] = useState(0);  // useEffect는 원하는 값을 변화하는 순간에만 선택적으로
    const [text, setText] = useState("");   // 사용할 수 있게 해줌 

    useEffect(()=> {
        console.log("Mount!");
    }, []);

    useEffect(()=>{
        console.log("Update!");
    })

    useEffect(()=>{
        console.log(`count is update: ${count}`);
        if(count > 5){
            alert("count가 5를 넘었습니다. 따라서 1로 초기화하빈다");

        }
    }, [count])

    useEffect(()=>{
        console.log(`text is update: ${text}`);
    }, [text]);

    return <div style={{ padding: 20 }}>
        <div>
            {count}
            <button onClick= {() => setCount(count +1)}>+</button>
        </div>
        <div>
            <input value={text} onChange={(e)=>setText(e.target.value)} />
        </div>
        </div>
}

export default Lifecycle;
